const inputs=document.querySelector(".contact__form").querySelectorAll("input"),textArea=document.querySelector(".contact__form").querySelector("textarea"),userInputs=[...inputs,textArea],reg=/^[^\s@]+@[^\s@]+\.[^\s@]+$/,email=userInputs.filter(e=>e.getAttribute("name").includes("email"))[0];userInputs.forEach(e=>{e.addEventListener("blur",()=>{e.value?reg.test(email.value)||email.classList.add("invalid"):e.classList.add("invalid")}),e.addEventListener("focus",()=>{e.classList.contains("invalid")&&e.classList.remove("invalid")})});