const gallery=document.querySelector(".persons__list"),persons=gallery.querySelectorAll(".person"),dotsBlock=document.querySelector(".team__slider").querySelector(".dots"),personMarginRight=window.getComputedStyle(gallery.firstElementChild).getPropertyValue("margin-right").slice(0,2),mainWidth=document.querySelector(".main").offsetWidth,personWidth=gallery.firstElementChild.offsetWidth+ +personMarginRight,visible=mainWidth<=767?1:mainWidth>=767&&mainWidth<=1279?2:3,moveSlider=()=>{const e=gallery.parentElement.previousElementSibling,t=gallery.parentElement.nextElementSibling;let l,i=0,r=0;e.addEventListener("click",()=>{if(r-=personWidth,l.classList.remove("active"),i--,r<0){for(let e of dotsBlock.children)e.classList.add("active");r=personWidth*persons.length-visible*personWidth,i=dotsBlock.children.length-1}gallery.style.marginLeft=-r+"px",l=dotsBlock.children[i]}),t.addEventListener("click",()=>{if(r+=personWidth,i++,r>personWidth*persons.length-visible*personWidth){for(let e of dotsBlock.children)e.classList.remove("active");r=0,i=0}gallery.style.marginLeft=-r+"px",l=dotsBlock.children[i],l.classList.add("active")})},addDots=()=>{for(let e=visible;e<=persons.length;e++)dotsBlock.innerHTML+='<i class="fas fa-circle"></i>'};moveSlider(),addDots();